def remove_duplicates(lst):
    return set(lst)

# Example usage:
# lst = ['cat', 'dog', 'fish', 'cat', 'dog']
# print(remove_duplicates(lst))  # Output: ['cat', 'dog', 'fish']

def remove_multiple_spaces(s):
    return " ".join(word for word in s.split() if word)

s = "  cat dog  fish"
print(remove_multiple_spaces(s))  # Output: "cat dog fish"

def count_vowels(s: str) -> int:
    count = 0
    for letter in s:
        if letter in "aeiou":
            count += 1
    return count

# Example:
# count_vowels("hello") # Returns 5
# count_vowels("world") # Returns 5
# count_vowels("apple") # Returns 2

