import urllib.parse
def tiny_url(url):
    return urllib.parse.urlparse(url).netloc + '/' + urllib.parse.quote(url)

def is_infinite(func):
    """
    This function determines if a given function will run forever or not.
    """
    try:
        for _ in range(1000000):
            func()
            if func() == 1:
                return False
        return True
    except Exception:
        return True

if __name__ == '__main__':
    func = lambda x: 2*x+1
    print(is_infinite(func))

