import os

def search_files(folder_name, extension):
    files = os.listdir(folder_name)
    files_to_copy = []
    for file in files:
        if file.endswith(extension):
            files_to_copy.append(os.path.join(folder_name, file))
    return files_to_copy

def copy_files(source, destination, files):
    for file in files:
        print("Copying", file)
        os.rename(os.path.join(source, file), os.path.join(destination, file))

if __name__ == '__main__':
    folder_name = 'folder'
    extension = 'jpeg'
    source = folder_name + '/'
    destination = 'destination/'

    files_to_copy = search_files(folder_name, extension)
    copy_files(source, destination, files_to_copy)

